import React, { useState } from "react"
import { AuthHeader, ButtonComponent, CardComponent, InputComponent } from "../../components"

import "./SignUpPage.scss"

export const SignUpPage = () => {
    const [isValid, setIsValid] = useState(true)
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
      }

      const handleConfirmPasswordChange = (event) => {
        setConfirmPassword(event.target.value);
      }

      const handleSubmit = (event) => {
        event.preventDefault();
        if (password === confirmPassword) {
          setIsValid(true);
          const errorMessage = password === confirmPassword ? '' : 'Пароли не совпадают, пожалуйста, повторите попытку';
          setIsValid(errorMessage === '');
          // отправляем данные на сервер
        } else {
          setIsValid(false);
          console.log("Passwords do not match");
        }
      }


    return (
        <div className="signInPage">
            <CardComponent className="cardComponent">
                <AuthHeader />
                <InputComponent placeholder="Имя" onChange={(e) => setName(e.target.value)} />
                <InputComponent placeholder="Email" onChange={(e) => setEmail(e.target.value)} />
                <InputComponent type="password" placeholder="Пароль" onChange={handlePasswordChange} value={password} />
                <InputComponent type="password" placeholder="Подтверждение пароля" onChange={handleConfirmPasswordChange} value={confirmPassword} />
                    { !isValid && <div style={{color: "red"}}>Пароли не совпадают, пожалуйста, повторите попытку</div> }
                <ButtonComponent title="Зарегистрироваться" onClick={handleSubmit}/>
                <div className="pol-signUp">Создавая аккаунт в сервисе, Вы соглашаетесь с <br/>
                     <span style={{color: "#378FC9"}}>Условиями использования </span>
                </div>
            </CardComponent>
        </div>
    )
}
