import React, { useState } from "react"
import { AuthHeader, ButtonComponent, CardComponent, InputComponent } from "../../components"

import "./SignUpPage.scss"

export const SignUpPage = () => {

    const [input, setInput] = useState({
        username: '',
        password: '',
        confirmPassword: ''
      });

      const [error, setError] = useState({
        username: '',
        password: '',
        confirmPassword: ''
      })

    const onInputChange = e => {
        const { name, value } = e.target;
        setInput(prev => ({
          ...prev,
          [name]: value
        }));
        validateInput(e);
      }

      const validateInput = e => {
        let { name, value } = e.target;
        setError(prev => {
          const stateObj = { ...prev, [name]: "" };

          switch (name) {
            case "username":
              if (!value) {
                stateObj[name] = "Please enter Username.";
              }
              break;

            case "password":
              if (!value) {
                stateObj[name] = "Please enter Password.";
              } else if (input.confirmPassword && value !== input.confirmPassword) {
                stateObj["confirmPassword"] = "Password and Confirm Password does not match.";
              } else {
                stateObj["confirmPassword"] = input.confirmPassword ? "" : error.confirmPassword;
              }
              break;

            case "confirmPassword":
              if (!value) {
                stateObj[name] = "Please enter Confirm Password.";
              } else if (input.password && value !== input.password) {
                stateObj[name] = "Password and Confirm Password does not match.";
              }
              break;

            default:
              break;
          }

          return stateObj;
        });
      }

    return (
        <div className="signInPage">
            <CardComponent className="cardComponent">
                <AuthHeader />
                <InputComponent placeholder="Имя" 
                    value={input.username}
                    onChange={onInputChange}
                    onBlur={validateInput}
                />
                {error.username && <span className='err'>{error.username}</span>}
                <InputComponent placeholder="Password"
                    type="password"
                    value={input.password}
                    onChange={onInputChange}
                    onBlur={validateInput} 
                />
                {error.password && <span className='err'>{error.password}</span>}
                {/* <InputComponent type="password" placeholder="Пароль"  /> */}
                <InputComponent type="password" placeholder="Подтверждение пароля" />

                <ButtonComponent title="Зарегистрироваться" />
                <div className="pol-signUp">Создавая аккаунт в сервисе, Вы соглашаетесь с <br/>
                     <span style={{color: "#378FC9"}}>Условиями использования </span>
                </div>
            </CardComponent>
        </div>
    )
}
