import React, { useState } from "react"
import { AuthHeader, ButtonComponent, CardComponent, InputComponent } from "../../components"

import "./SignUpPage.scss"

export const SignUpPage = () => {


  const [input, setInput] = useState({
    username: '',
    password: '',
    confirmPassword: ''
  });

  const [error, setError] = useState({
    username: '',
    password: '',
    confirmPassword: ''
  })

  const onInputChange = e => {
    const { name, value } = e.target;
    setInput(prev => ({
      ...prev,
      [name]: value
    }));
    validateInput(e);
  }

  const validateInput = e => {
    let { name, value } = e.target;
    setError(prev => {
      const stateObj = { ...prev, [name]: "" };

      switch (name) {
        case "username":
          if (!value) {
            stateObj[name] = "Please enter Username.";
          }
          break;

        case "password":
          if (!value) {
            stateObj[name] = "Please enter Password.";
          } else if (input.confirmPassword && value !== input.confirmPassword) {
            stateObj["confirmPassword"] = "Password and Confirm Password does not match.";
          } else {
            stateObj["confirmPassword"] = input.confirmPassword ? "" : error.confirmPassword;
          }
          break;

        case "confirmPassword":
          if (!value) {
            stateObj[name] = "Please enter Confirm Password.";
          } else if (input.password && value !== input.password) {
            stateObj[name] = "Password and Confirm Password does not match.";
          }
          break;

        default:
          break;
      }

      return stateObj;
    });
  }

  return (
    <CardComponent>
     <AuthHeader />

      <form> 
        <InputComponent
          type="text"
          name="username"
          placeholder='Enter Username'
          value={input.username}
          onChange={onInputChange}
          onBlur={validateInput} />
        {error.username && <div className="error-message-signUp">{error.username}</div>}

        <InputComponent
          type="password"
          name="password"
          placeholder='Enter Password'
          value={input.password}
          onChange={onInputChange}
          onBlur={validateInput} />
        {error.password && <span className='err'>{error.password}</span>}

        <InputComponent
          type="password"
          name="confirmPassword"
          placeholder='Enter Confirm Password'
          value={input.confirmPassword}
          onChange={onInputChange}
          onBlur={validateInput}/>
        {error.confirmPassword && <span className='err'>{error.confirmPassword}</span>}

        <button>Submit</button>
      </form>
      <div className="pol-signUp">
        Создавая аккаунт в сервисе, Вы соглашаетесь с <br/>
        <span></span>Условиями использования 
      </div>
    </CardComponent>
  );
}
