import React, { useEffect, useState, useRef } from "react";
import {useForm} from 'react-hook-form';
import { useDispatch, useSelector } from "react-redux";
import { NavLink, useNavigate } from "react-router-dom";
import { AuthHeader, ButtonComponent, CardComponent, InputComponent } from "../../components";
import { signIn } from "../../utils/api/user";
import validator from 'validator';


import "./SignInPage.scss";

export const SignInPage = () => {
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const user = useSelector((state) => state.user)
    const isLogin = useSelector((state) => state.user.isLogin)

    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    const [isValid, setIsValid] = useState(true)
    const [isEmailValid, setIsEmailValid] = useState(true);

    const handleEmailBlur = (event) => {
        setIsValid(event.target.value.length > 0);
        setIsEmailValid(validator.isEmail(event.target.value));
    }

    const handleEmailChange = (event) => {
        setEmail(event.target.value);
    }

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
    }

    const handleAuth = () => {
        dispatch(signIn(email, password))
    }

    useEffect(() => {
        if (isLogin) {
            navigate("/")
        }
    }, [isLogin, navigate])

    return (
        <div className="signInPage">
            <CardComponent>
                <AuthHeader />
                <InputComponent 
                    type="email" 
                    value={email} 
                    onBlur={handleEmailBlur} 
                    placeholder="E-mail" 
                    name="email" 
                    onChange={handleEmailChange} 
                />
                {!isValid && <div className="error-message-signIn"> Поле не может быть пустым </div> } 
                {!isEmailValid && <div className="error-message-signIn"> Введите корректный email </div> } 
                <InputComponent style={{marginTop: "10px"}} type="password" placeholder="Пароль" value={password} onChange={handlePasswordChange} />
                {/* {user.error && <span className="error-message">Некорректно введены данные</span>} */}
                <ButtonComponent title="Войти" onClick={handleAuth}/>
                <div className="remember">
                    <div className="remember-me">
                        <div className="inp-container">
                            <input className="checkbox" type="checkbox" />
                        </div>
                        <div className="span-container">
                            <span className="title">Запомнить меня</span>
                        </div>
                    </div>
                    <NavLink to='/forgot-pas'><div className="forgot-pass">Забыли пароль?</div></NavLink>
                </div>
            </CardComponent>
        </div>
    )
}
